"use client";
import React, { useState } from "react";

import "./style.css";
const pointsOfInterest = {
  Nigeria: [
    { x: 360, y: 530, label: "Point 1" },
    { x: 370, y: 540, label: "Point 2" },
  ],
  Niger: [
    { x: 390, y: 320, label: "Point 1" },
    { x: 400, y: 330, label: "Point 2" },
  ],
  // Agrega mÃ¡s puntos para otras regiones
};

export const Map = () => {
  const [selectedRegion, setSelectedRegion] = useState(null);
  const [zoomCoords, setZoomCoords] = useState(null);

  const handleRegionClick = (event) => {
    const svg = event.target.closest("svg");
    const point = svg.createSVGPoint();
    point.x = event.clientX;
    point.y = event.clientY;

    const coords = point.matrixTransform(svg.getScreenCTM().inverse());

    console.log(coords);
    setSelectedRegion(event.target.id);
    setZoomCoords({ x: coords.x, y: coords.y });
    console.log(event.target.id);
  };

  const handleResetZoom = () => {
    setSelectedRegion(null);
  };

  return (
    <div>
      <button
        onClick={handleResetZoom}
        className={` ${
          selectedRegion ? "" : "hidden"
        } mb-4 p-2 bg-blue-500 text-white rounded`}
      >
        Deshacer Zoom
      </button>

      <div className="relative">
        <svg
          viewBox="0 0 1200 1230"
          xmlns="http://www.w3.org/2000/svg"
          width="100%"
          height="100%"
        >
          <g>
            <AfricaMap
              selectedRegion={selectedRegion}
              handleRegionClick={handleRegionClick}
            />
            {selectedRegion &&
              pointsOfInterest[selectedRegion].map((point, index) => (
                <circle
                  key={index}
                  cx={point.x}
                  cy={point.y}
                  r="5"
                  fill="yellow"
                  className="point"
                >
                  <title>{point.label}</title>
                </circle>
              ))}
          </g>
        </svg>
      </div>
      {/* <AfricaMap handleRegionClick={handleRegionClick} /> */}
    </div>
  );
};
